<script> 

export default {
  el: "#app",
  data() {
    return {
      current: 1,
      pageSize: 12,
      posts: [],
    };
  },

  created () {
    fetch("https://jsonplaceholder.typicode.com/posts")  //thi this be the laaravalsite api "web api"
    .then((response) => response.json())
    .then((data) => {
      //console.log(data)
      this.posts = data;
    });
  },

  computed: {
    indexStart() {
      return (this.current - 1) * this.pageSize;
    },
    indexEnd() {
      return this.indexStart + this.pageSize;
    },
    paginated() {
      return this.posts.slice(this.indexStart, this.indexEnd);
    }
  },
  methods: {
    prev() {
      this.current--;
    },
    next() {
      this.current++;
    }
  }
};

</script>
